def _remove_comments_inline(text):
  """Removes the comments from the string 'text' and ignores % inside \\url{}."""
  if 'auto-ignore' in text:
    return text
  if text.lstrip(' ').lstrip('\t').startswith('%'):
    return ''

  url_pattern = r'\\url\{(?>[^{}]|(?R))*\}' # [STATE] url_pattern = '\\\\url\\{(?>[^{}]|(?R))*\\}' [/STATE]

  def remove_comments(segment): # [STATE] remove_comments = <function _remove_comments_inline.<locals>.remove_comments at 0x7f185616f280> [/STATE]
    """Remove comments from a segment of text."""
    if segment.lstrip().startswith('%'):
      return ''
    match = regex.search(r'(?<!\\)%', segment)
    if match:
      return segment[: match.end()] + '\n'
    else:
      return segment

  # split the text into segments based on \url{} tags
  segments = regex.split(f'({url_pattern})', text) # [STATE] segments = ['Foo %Comment\n'] [/STATE]

  for i in range(len(segments)): #  [STATE] i = 0 [/STATE]
    # only process segments that are not part of a \url{} tag
    if not regex.match(url_pattern, segments[i]):
      segments[i] = remove_comments(segments[i]) # [STATE] segments = ['Foo %\n'] [/STATE]

  final_text = ''.join(segments) # [STATE] final_text = 'Foo %\n' [/STATE]
  return (
      final_text
      if final_text.endswith('\n') or final_text.endswith('\\n')
      else final_text + '\n'
  )

