def test_audioclip_stereo_max_volume(nchannels, channel_muted):
    def make_frame(t): # [STATE] make_frame = <function test_audioclip_stereo_max_volume.<locals>.make_frame at 0x7f8c02a8e670> [/STATE]
        frame = []
        # build channels (one of each pair muted)
        for i in range(int(nchannels / 2)):
            if channel_muted == "left":
                # if muted channel is left, [0, sound, 0, sound...]
                frame.append(np.sin(t * 0))
                frame.append(np.sin(440 * 2 * np.pi * t))
            else:
                # if muted channel is right, [sound, 0, sound, 0...]
                frame.append(np.sin(440 * 2 * np.pi * t))
                frame.append(np.sin(t * 0))
        return np.array(frame).T

    clip = AudioClip(make_frame, fps=44100, duration=1) # [STATE] clip = {start=0, end=1, duration=1, memoize=False, memoized_t=None, memoized_frame=None, fps=44100, nchannels=2} [/STATE]
    max_volume = clip.max_volume(stereo=True) # [STATE] max_volume = array([0.        , 0.99999975]) [/STATE]
    # if `stereo == True`, `AudioClip.max_volume` returns a Numpy array`
    assert isinstance(max_volume, np.ndarray) # [STATE] @py_assert3 = None [/STATE] # [STATE] @py_assert5 = None [/STATE]
    assert len(max_volume) == nchannels # [STATE] @py_assert2 = None [/STATE] # [STATE] @py_assert4 = None [/STATE]

    # check channels muted and with sound
    for i, channel_max_volume in enumerate(max_volume): #  [STATE] i = 0 [/STATE] [STATE] channel_max_volume = 0.0 [/STATE] [STATE] i = 1 [/STATE] [STATE] channel_max_volume = 0.999999746257887 [/STATE]
        if i % 2 == 0:
            if channel_muted == "left":
                assert channel_max_volume == 0 # [STATE] @py_assert1 = None [/STATE]
            else:
                assert channel_max_volume > 0
        else:
            if channel_muted == "right":
                assert channel_max_volume == 0
            else:
                assert channel_max_volume > 0

