def _validate_htype_overwrites(htype: str, htype_overwrite: dict):
    """Raises errors if ``htype_overwrite`` has invalid keys or was missing required values."""

    defaults = HTYPE_CONFIGURATIONS[htype] # [STATE] defaults = {'dtype': None, 'sample_compression': None, 'chunk_compression': None, 'typestr': None, 'max_chunk_size': None, 'tiling_threshold': None, 'is_sequence': False, 'is_link': False, 'hidden': False, 'links': None, 'verify': False} [/STATE]

    for key, value in htype_overwrite.items(): #  [STATE] key = 'sample_compression' [/STATE] [STATE] value = 'unspecified' [/STATE] [STATE] key = 'chunk_compression' [/STATE] [STATE] key = 'dtype' [/STATE] [STATE] value = 'int64' [/STATE] [STATE] key = 'hidden' [/STATE] [STATE] value = True [/STATE] [STATE] key = 'max_chunk_size' [/STATE] [STATE] value = 4000000 [/STATE] [STATE] key = 'is_sequence' [/STATE] [STATE] value = False [/STATE] [STATE] key = 'is_link' [/STATE] [STATE] key = 'verify' [/STATE]
        if key not in defaults:
            raise TensorMetaInvalidHtypeOverwriteKey(htype, key, list(defaults.keys()))

        if isinstance(value, str) and value == UNSPECIFIED:
            if defaults[key] == REQUIRE_USER_SPECIFICATION:
                raise TensorMetaMissingRequiredValue(htype, key)

    sc = htype_overwrite["sample_compression"] # [STATE] sc = 'unspecified' [/STATE]
    cc = htype_overwrite["chunk_compression"] # [STATE] cc = 'unspecified' [/STATE]
    compr = sc if cc in (None, UNSPECIFIED) else cc # [STATE] compr = 'unspecified' [/STATE]
    actual_htype = f"link[{htype}]" if htype_overwrite["is_link"] else htype # [STATE] actual_htype = 'generic' [/STATE]
    if htype.startswith("image") and sc == UNSPECIFIED and cc == UNSPECIFIED:
        raise TensorMetaMissingRequiredValue(
            actual_htype, ["chunk_compression", "sample_compression"]  # type: ignore
        )
    if htype in ("audio", "video", "point_cloud", "mesh", "nifti"):
        if cc not in (UNSPECIFIED, None):
            raise UnsupportedCompressionError("Chunk compression", htype=htype)
        elif sc == UNSPECIFIED:
            raise TensorMetaMissingRequiredValue(
                actual_htype, "sample_compression"  # type: ignore
            )
    supported_compressions = HTYPE_SUPPORTED_COMPRESSIONS.get(htype) # [STATE] supported_compressions = None [/STATE]
    if (
        compr
        and compr != UNSPECIFIED
        and supported_compressions
        and compr not in supported_compressions
    ):
        raise UnsupportedCompressionError(compr, htype=htype)

